        try {
            $redis = new Redis();
            $redis->connect(self::$configs[self::$link_name]['host'], self::$configs[self::$link_name]['port']);
            if ($redis->ping()) {
                self::$links[self::$link_name] = $redis;
                return $redis;
            } else {
                self::$error = "Failed to connect to Redis server at {$self::$configs[self::$link_name]['host']}:{$self::$configs[self::$link_name]['port']}.";
                return false;
            }
        } catch (Exception $e) {
            self::$error = $e->getMessage();
            return false;
        }
    }

    public static function set($key, $value, $expire = 0) {
        $redis = self::init();
        if (!$redis) {
            return null;
        }
        $key = self::$prefix . $key;
        if ($redis->set($key, $value)) {
            if ($expire > 0) {
                $redis->expire($key, $expire);
            }
            return true;
        } else {
            self::$error = "Failed to set key '$key' in Redis.";
            return null;
        }
    }

    public static function get($key) {
        $redis = self::init();
        if (!$redis) {
            return null;
        }
        $key = self::$prefix . $key;
        $value = $redis->get($key);
        if ($value === false && $redis->exists($key)) {
            self::$error = "Failed to retrieve key '$key' from Redis.";
            return null;
        }
        return $value;
    }

    public static function del($key) {
        $redis = self::init();
        if (!$redis) {
            return null;
        }
        $key = self::$prefix . $key;
        $deleted = $redis->delete($key);
        if ($deleted === false) {
            self::$error = "Failed to delete key '$key' from Redis.";
            return null;
        }
        return $deleted;
    }

    private static function _get_default_config() {
        return [
            'host' => '127.0.0.1',
            'port' => 6379,
        ];
    }

    public static function clear_link() {
        foreach (self::$links as $link) {
            $link->close();
        }
        self::$links = [];
    }
}
