use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\Mime\AbstractPart;
use Symfony\Component\Mime\FormDataPart;
use Symfony\Component\Mime\TextPart;

class HttpClientKernel implements HttpKernelInterface
{
    private $client;

    public function __construct(?HttpClientInterface $client = null)
    {
        if ($client === null && !interface_exists(HttpClientInterface::class)) {
            throw new \RuntimeException('Symfony HttpClient component is not installed.');
        }
        $this->client = $client ?? HttpClient::create();
    }

    public function handle(Request $request, int $type = self::MAIN_REQUEST, bool $catch = true): Response
    {
        try {
            $response = $this->client->request(
                $request->getMethod(),
                $request->getUri(),
                [
                    'headers' => $this->getHeaders($request),
                    'body' => $this->getBody($request),
                ]
            );
            return new Response($response->getContent(), $response->getStatusCode(), $response->getHeaders());
        } catch (ClientException | RedirectionException | ServerException | TransportException $e) {
            if ($catch) {
                return new Response('Error: ' . $e->getMessage(), 500);
            }
            throw $e;
        }
    }

    private function getBody(Request $request): ?AbstractPart
    {
        if ($request->isMethodSafe()) {
            return null;
        }
        if ($request->files->count() > 0) {
            $formData = new FormDataPart();
            foreach ($request->request->all() as $key => $value) {
                $formData->addFile($key, $value);
            }
            foreach ($request->files->all() as $file) {
                $formData->addFile($file->getClientOriginalName(), $file->getContent());
            }
            return $formData;
        }
        return new TextPart($request->getContent());
    }

    private function getHeaders(Request $request): array
    {
        $headers = [];
        foreach ($request->headers->all() as $name => $values) {
            $headers[$name] = implode(', ', $values);
        }
        if ($cookies = $request->cookies->all()) {
            $headers['Cookie'] = implode('; ', array_map(function ($value, $name) {
                return urlencode($name) . '=' . urlencode($value);
            }, $cookies, array_keys($cookies)));
        }
        return $headers;
    }
