use Symfony\Component\Mime\AbstractPart;
use Symfony\Component\Mime\FormDataPart;
use Symfony\Component\Mime\TextPart;

class HttpClientKernel
{
    private $client;

    public function __construct(HttpClientInterface $client = null)
    {
        $this->client = $client ?? HttpClient::create();
    }

    public function handle(Request $request, int $type = HttpKernelInterface::MAIN_REQUEST, bool $catch = true): Response
    {
        $body = $this->getBody($request);
        $headers = $this->getHeaders($request);

        $response = $this->client->request(
            $request->getMethod(),
            $request->getUri(),
            [
                'headers' => $headers,
                'body' => $body,
                'allow_redirects' => ['maxRedirects' => 5],
            ]
        );

        return new Response(
            $response->getContent(),
            $response->getStatusCode(),
            $response->getHeaders()
        );
    }

    private function getBody(Request $request): ?AbstractPart
    {
        if ($request->isMethod('GET') || $request->isMethod('HEAD')) {
            return null;
        } elseif ($request->files->count() > 0) {
            $formData = [];
            foreach ($request->files as $key => $file) {
                $formData[$key] = fopen($file->getPathname(), 'r');
            }
            return new FormDataPart($formData);
        } else {
            return new TextPart($request->getContent());
        }
    }

    private function getHeaders(Request $request): array
    {
        $headers = $request->headers->all();
        if ($cookies = $request->cookies->all()) {
            $headers['Cookie'] = implode('; ', array_map(function ($value, $key) {
                return urlencode($key) . '=' . urlencode($value);
            }, $cookies, array_keys($cookies)));
        }
        return $headers;
    }
