        'git describe --tags',
        [
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w'],
        ],
        $pipes,
        $path
    );

    if (!\is_resource($process)) {
        return false;
    }

    $result = \trim(\stream_get_contents($pipes[1]));

    \fclose($pipes[1]);
    \fclose($pipes[2]);

    $returnCode = \proc_close($process);

    if ($returnCode !== 0) {
        return false;
    }

    return $result;
