        case 'OPTIONS':
            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'OPTIONS');
            break;
    }

    if ($headers !== false) {
        curl_setopt($ch, CURLOPT_HTTPHEADER, is_array($headers) ? $headers : [$headers]);
    }

    if ($options !== false) {
        curl_setopt_array($ch, $options);
    }

    $response = curl_exec($ch);
    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    $error = curl_error($ch);
    $curlInfo = curl_getinfo($ch);

    curl_close($ch);

    if ($error) {
        return [
            'data' => $error,
            'status' => false,
            'code' => 0,
            'header' => $curlInfo
        ];
    } else {
        $responseData = [
            'data' => $response,
            'status' => $httpCode >= 200 && $httpCode < 300,
            'code' => $httpCode,
            'header' => $curlInfo
        ];

        if ($json && !empty($response)) {
            $responseData['data'] = json_decode($response, true);
        }

        return $responseData;
    }
