class DefaultPhpProcess extends AbstractPhpProcess {
    public function runJob($job, $settings = []) {
        $this->setupSettings($settings);
        list($stdout, $stderr) = $this->runProcess($job, $settings);
        return ['output' => $stdout, 'error' => $stderr];
    }

    protected function setupSettings($settings) {
        $this->tempFile = isset($settings['tempFile']) ? $settings['tempFile'] : null;
        $this->stdin = isset($settings['stdin']) ? $settings['stdin'] : null;
        $this->env = isset($settings['env']) ? $settings['env'] : [];
        $this->timeout = isset($settings['timeout']) ? $settings['timeout'] : 30;
    }

    protected function runProcess($job, $settings) {
        $descriptorspec = [
            0 => ['pipe', 'r'], // stdin is a pipe that the child will read from
            1 => ['pipe', 'w'], // stdout is a pipe that the child will write to
            2 => ['pipe', 'w']  // stderr is a pipe that the child will write to
        ];

        $process = proc_open($job, $descriptorspec, $pipes, null, $this->env);

        if (is_resource($process)) {
            $this->process($pipes[0], $this->stdin);
            fclose($pipes[0]);

            stream_set_blocking($pipes[1], false);
            stream_set_blocking($pipes[2], false);

            $stdout = stream_get_contents($pipes[1]);
            $stderr = stream_get_contents($pipes[2]);

            fclose($pipes[1]);
            fclose($pipes[2]);

            $status = proc_close($process);

            if ($status !== 0 && $this->timeout > 0) {
                throw new Exception("Process timed out after {$this->timeout} seconds");
            }

            return [$stdout, $stderr];
        } else {
            throw new Exception("Failed to start process");
        }
    }

    protected function process($pipe, $job) {
        if ($job !== null) {
            fwrite($pipe, $job);
        }
    }

    protected function cleanup() {
        if ($this->tempFile !== null && file_exists($this->tempFile)) {
            unlink($this->tempFile);
        }
    }

    protected function useTemporaryFile() {
        return true;
    }
}
