use GuzzleHttp\Client;

class HttpClient {
    private $client;

    public function __construct() {
        $this->client = new Client();
    }

    public function getSourceFile($fileName, $savePath, $url) {
        try {
            $response = $this->client->request('GET', $url);
            if ($response->getStatusCode() == 200) {
                file_put_contents($savePath . '/' . $fileName, $response->getBody());
                return true;
            }
        } catch (\Exception $e) {
            error_log('Error downloading file: ' . $e->getMessage());
        }
        return false;
    }

    public function getLatestReleaseTagName($repo) {
        return $this->getLatestTag($repo, 'release');
    }

    public function getLatestPreReleaseTagName($repo) {
        return $this->getLatestTag($repo, 'pre-release');
    }

    private function getLatestTag($repo, $type) {
        $url = "https://api.github.com/repos/$repo/releases";
        try {
            $response = $this->client->request('GET', $url);
            if ($response->getStatusCode() == 200) {
                $releases = json_decode($response->getBody(), true);
                foreach ($releases as $release) {
                    if (($type === 'release' && !$release['prerelease']) || ($type === 'pre-release' && $release['prerelease'])) {
                        return $release['tag_name'];
                    }
                }
            }
        } catch (\Exception $e) {
            error_log('Error fetching releases: ' . $e->getMessage());
        }
        return null;
    }
}
