        $headerPart = $parts[0];
        $bodyPart = isset($parts[1]) ? $parts[1] : '';

        foreach (explode("\r\n", $headerPart) as $line) {
            if (strpos($line, ': ') !== false) {
                list($key, $value) = explode(': ', $line, 2);
                $headers[$key] = $value;
            }
        }

        if ($status == 200) {
            return json_decode($bodyPart, true);
        } else {
            return ['status' => $status, 'headers' => $headers, 'body' => $bodyPart];
        }
    }

    public function error($header, $body) {
