        $this->timeout = $timeout;
    }

    public function request($path, $method, $headers = null, $body = null) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, self::QINIU_RSF_HOST . $path);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);

        if ($headers !== null) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }

        if ($method === 'POST' || $method === 'PUT') {
            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        } elseif ($method === 'HEAD') {
            curl_setopt($ch, CURLOPT_NOBODY, true);
        }

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $curlError = curl_error($ch);

        curl_close($ch);

        if ($curlError !== '') {
            $this->error = 'Curl Error';
            $this->errorStr = $curlError;
            return false;
        }

        if ($httpCode >= 400) {
            $this->error('HTTP Error', $response);
            return false;
        }

        if ($method === 'GET') {
            return $response;
        }

        return $this->response($response);
    }

    public function response($text) {
        preg_match('/^HTTP\/\d+\.\d+ (\d+) .*\r\n/', $text, $matches);
        $status = $matches[1];
        $headers = [];
        $body = '';

        $parts = explode("\r\n\r\n", $text, 2);
        if (count($parts) === 2) {
            list($headerPart, $body) = $parts;
            $lines = explode("\r\n", $headerPart);
            foreach ($lines as $line) {
                if (strpos($line, ': ') !== false) {
                    list($key, $value) = explode(': ', $line, 2);
                    $headers[$key] = $value;
                }
            }
        }

        return [
            'status' => $status,
            'headers' => $headers,
            'body' => $body
        ];
    }

    public function error($header, $body) {
        preg_match('/^HTTP\/\d+\.\d+ (\d+) .*$/', $header, $matches);
        $statusCode = $matches[1];

        switch ($statusCode) {
            case '400':
                $this->error = 'Bad Request';
                break;
            case '401':
                $this->error = 'Unauthorized';
                break;
            case '403':
                $this->error = 'Forbidden';
                break;
            case '404':
                $this->error = 'Not Found';
                break;
            default:
                $this->error = 'Unknown Error';
                break;
        }

        $this->errorStr = $body;
    }
}
