            list($key, $value) = explode(': ', $line);
            $headers[$key] = $value;
        }

        $body = implode("\r\n", array_slice($lines, count($headers) + 2));

        if ($status != 200) {
            $this->error('Response Error', $body);
            return false;
        }

        return json_decode($body, true);
    }

