    public function image($dir = null, $width = 640, $height = 480, $category = null, $fullPath = true, $randomize = true, $word = null, $gray = false, $format = 'png') {
        $url = $this->imageUrl($width, $height, $category, $randomize, $word, $gray, $format);
        if ($url === false) {
            return false;
        }

        $filePath = $this->downloadImage($url, $dir, $fullPath);

        return $filePath;
    }

    public function imageUrl($width, $height, $category, $randomize, $word, $gray, $format) {
        $url = self::BASE_URL . $width . "x" . $height;

        if ($category !== null && in_array($category, self::$categories)) {
            $url .= "/" . urlencode($category);
        } else {
            $url .= "/any";
        }

        if ($randomize) {
            $url .= "?text=" . urlencode(rand(1, 999));
        }

        if ($word !== null) {
            $url .= "?text=" . urlencode($word);
        }

        if ($gray) {
            $url .= "&grayscale";
        }

        $url .= "." . $format;

        return $url;
    }

    private function downloadImage($url, $dir = null, $fullPath = true) {
        if (!function_exists('curl_init')) {
            return $this->fileDownload($url, $dir, $fullPath);
        }

        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $imageData = curl_exec($ch);
        curl_close($ch);

        if ($imageData === false) {
            return false;
        }

        return $this->saveImage($imageData, $dir, $fullPath);
    }

    private function fileDownload($url, $dir = null, $fullPath = true) {
        $imageData = @file_get_contents($url);
        if ($imageData === false) {
            return false;
        }

        return $this->saveImage($imageData, $dir, $fullPath);
    }

    private function saveImage($imageData, $dir = null, $fullPath = true) {
        if ($dir === null) {
            $dir = sys_get_temp_dir();
        }

        $fileName = uniqid() . ".png"; // Default to PNG format if not specified
        $filePath = $dir . '/' . $fileName;

        if (!$fullPath) {
            $filePath = basename($filePath);
        }

        if (@file_put_contents($filePath, $imageData) === false) {
            return false;
        }

        return $filePath;
    }
