        $currencies = $this->currencyRepository->getAll();

        foreach ($currencies as $currency) {
            if ($currency->getId() == $this->baseCurrencyId) continue;

            try {
                $rate = $this->fetchConversionRate($currency);
                $this->conversionRateRepository->storeOrUpdate($this->baseCurrencyId, $currency->getId(), $rate);
            } catch (RequestException $e) {
                error_log("Failed to fetch conversion rate for {$currency->getCode()}: " . $e->getMessage());
            }
        }
    }

    private function fetchConversionRate(Currency $targetCurrency): float {
        $client = new Client();
        $response = $client->request('GET', "https://api.exchangeratesapi.io/latest?base={$this->baseCurrencyId}&symbols={$targetCurrency->getCode()}");
        $data = json_decode($response->getBody()->getContents(), true);

        if (!isset($data['rates'][$targetCurrency->getCode()])) {
            throw new RequestException("Invalid response format");
        }

        return $data['rates'][$targetCurrency->getCode()];
    }
}
