class timthumb {
    private $curlFH = false;
    private $curlDataWritten = 0;
    private $lastURLError = false;

    public static function curlWrite($h, $d) {
        $self = self::getInstance();
        $maxSize = 1024 * 1024; // 1MB
        if ($self->curlDataWritten + strlen($d) > $maxSize) {
            return 0;
        }
        $self->curlDataWritten += fwrite($self->curlFH, $d);
        return strlen($d);
    }

    public function getURL($url, $tempfile) {
        if (!function_exists('curl_init')) {
            return $this->error("cURL is not available");
        }
        $this->curlFH = fopen($tempfile, 'w');
        if (!$this->curlFH) {
            return $this->error("Failed to open file for writing: " . $tempfile);
        }
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, array(__CLASS__, 'curlWrite'));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $result = curl_exec($ch);
        $errno = curl_errno($ch);
        $errstr = curl_error($ch);
        curl_close($ch);
        fclose($this->curlFH);
        if ($errno != CURLE_OK) {
            $this->lastURLError = "cURL Error #{$errno}: {$errstr}";
            return false;
        }
        return true;
    }

    private function error($message) {
        $this->lastURLError = $message;
        return false;
    }

    private static function getInstance() {
        static $instance = null;
        if ($instance === null) {
            $instance = new self();
        }
        return $instance;
    }
}
