    private $curlFH;
    private $curlDataWritten = 0;
    private $lastURLError = false;

    public static function curlWrite($h, $d) {
        $self = self::getInstance();
        $maxSize = 1024 * 1024; // 1MB limit
        if ($self->curlDataWritten + strlen($d) > $maxSize) {
            return 0;
        }
        $self->curlDataWritten += fwrite($self->curlFH, $d);
        return strlen($d);
    }

    public function getURL($url, $tempfile) {
        $this->curlFH = fopen($tempfile, 'w');
        if (!$this->curlFH) {
            $this->lastURLError = "Failed to open temp file for writing.";
            return false;
        }
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, array('timthumb', 'curlWrite'));
        $result = curl_exec($ch);
        fclose($this->curlFH);
        curl_close($ch);

        if ($result === false) {
            $this->lastURLError = curl_error($ch);
            return false;
        }

        return true;
    }

    private function getInstance() {
        static $instance = null;
        if ($instance === null) {
            $instance = new self();
        }
        return $instance;
    }

    private function set404() {
        // Implement 404 error handling logic here
    }

    private function error() {
        // Implement general error handling logic here
    }
}
