    private $apiUrl = "https://api.xiami.com/web";

    public function xiami_http(int $type, string $id): array|bool {
        $params = [
            'method' => 'xiami.api.song.get',
            'app_key' => 'your_app_key', // Replace with your actual app key
            'format' => 'json',
            'v' => '2.0',
            'sign_method' => 'md5',
            'timestamp' => date('Y-m-d H:i:s'),
            'nonce' => uniqid(),
            'id' => $id,
            'type' => $type
        ];

        $params['sign'] = $this->generateSign($params);

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $this->apiUrl);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);

        $response = curl_exec($ch);
        if ($response === false) {
            return false;
        }

        curl_close($ch);

        return json_decode($response, true);
    }

    private function generateSign(array $params): string {
        ksort($params);
        $stringToSign = '';
        foreach ($params as $key => $value) {
            if ($key !== 'sign') {
                $stringToSign .= $key . '=' . urlencode($value);
            }
        }
        $stringToSign .= 'secret=your_secret'; // Replace with your actual secret

        return md5($stringToSign);
    }
