    }

    private static function buildMultipartFormData($fields, $name, $fileName, $fileBody, $mimeType = null) {
        $boundary = md5(uniqid(time()));
        $multipartData = '';

        foreach ($fields as $key => $value) {
            $multipartData .= "--$boundary\r\n";
            $multipartData .= "Content-Disposition: form-data; name=\"$key\"\r\n";
            $multipartData .= "\r\n";
            $multipartData .= $value . "\r\n";
