        if ($response === false) {
            throw new Exception(curl_error($ch));
        }

        curl_close($ch);

        return new Response($response, self::parseHeaders($response));
    }

    public static function userAgent() {
        $sdkVersion = '1.0';
        $system = php_uname();
        $phpVersion = PHP_VERSION;
        return "SDK/{$sdkVersion} {$system} PHP/{$phpVersion}";
    }

    private static function parseHeaders($raw) {
        $headers = [];
        $headerLines = explode("\r\n", $raw);
        $firstLine = array_shift($headerLines);
        list($status, $code) = explode(' ', $firstLine, 2);
        $headers['Status'] = $status;
        $headers['Code'] = $code;

        foreach ($headerLines as $line) {
            list($key, $value) = explode(': ', $line, 2);
            $headers[$key] = $value;
        }

        return $headers;
    }

    private static function escapeQuotes($str) {
        return str_replace('"', '\\"', $str);
    }

    private static function ucwordsHyphen($str) {
        return ucwords(str_replace('-', ' ', $str), ' ');
    }
}

class Request {
    private $method;
    private $url;
    private $body;
    private $headers;

    public function __construct($method, $url, $body = [], $headers = []) {
        $this->method = $method;
        $this->url = $url;
        $this->body = $body;
        $this->headers = $headers;
    }

    public function getUrl() {
        return $this->url;
    }

    public function getMethod() {
        return $this->method;
    }

    public function getBody() {
        if (is_array($this->body)) {
            if (isset($this->body['form_data'])) {
                return http_build_query($this->body['form_data']);
            } elseif (isset($this->body['files'])) {
                $boundary = uniqid();
                $multipartData = "--$boundary\r\n";
                foreach ($this->body['files'] as $name => $file) {
                    $multipartData .= "Content-Disposition: form-data; name=\"$name\"; filename=\"" . self::escapeQuotes($file['filename']) . "\"\r\n";
                    $multipartData .= "Content-Type: " . ($file['mime_type'] ?? 'application/octet-stream') . "\r\n\r\n";
                    $multipartData .= $file['content'] . "\r\n--$boundary\r\n";
                }
                $multipartData .= "--$boundary--\r\n";
                return $multipartData;
            }
        }
        return $this->body;
    }

    public function getHeaders() {
        $headers = [];
        foreach ($this->headers as $key => $value) {
            $headers[self::ucwordsHyphen($key)] = self::escapeQuotes($value);
        }
        return $headers;
    }
}

class Response {
    private $data;
    private $headers;

    public function __construct($data, $headers) {
        $this->data = $data;
        $this->headers = $headers;
    }

    public function getData() {
        return $this->data;
    }

    public function getHeaders() {
        return $this->headers;
    }
}
