class Request {
    public $method;
    public $url;
    public $headers;
    public $body;

    public function __construct($method, $url, $headers = [], $body = null) {
        $this->method = $method;
        $this->url = $url;
        $this->headers = $headers;
        $this->body = $body;
    }
}

class HttpClient {
    private static function userAgent() {
        return sprintf('SDK/1.0 System/%s PHP/%s', php_uname(), PHP_VERSION);
    }

    private static function parseHeaders($raw) {
        $headers = [];
        foreach (explode("\r\n", $raw) as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[strtolower(trim($key))] = trim($value);
            }
        }
        return $headers;
    }

    private static function escapeQuotes($str) {
        return str_replace('"', '\\"', $str);
    }

    private static function ucwordsHyphen($str) {
        return implode('-', array_map('ucfirst', explode('-', $str)));
    }

    private static function sendRequest(Request $request) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $request->url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->method);

        if (!empty($request->headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, array_map(function($k, $v) { return "$k: " . self::escapeQuotes($v); }, array_keys($request->headers), $request->headers));
        }

        if ($request->method === 'POST' || $request->method === 'PUT') {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
        }

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headers = self::parseHeaders(substr($response, 0, $headerSize));
        $body = substr($response, $headerSize);

        curl_close($ch);

        return new Response($httpCode, $headers, $body);
    }

    public static function get($url, $headers = []) {
        return self::sendRequest(new Request('GET', $url, $headers));
    }

    public static function delete($url, $headers = []) {
        return self::sendRequest(new Request('DELETE', $url, $headers));
    }

    public static function post($url, $body, $headers = []) {
        return self::sendRequest(new Request('POST', $url, $headers, $body));
    }

    public static function multipartPost($url, $fields, $name, $fileName, $fileBody, $mimeType = null, $headers = []) {
        $boundary = uniqid();
        $multipartBody = '';

        foreach ($fields as $key => $value) {
            $multipartBody .= "--$boundary\r\n";
            $multipartBody .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
            $multipartBody .= $value . "\r\n";
        }

        $multipartBody .= "--$boundary\r\n";
        $multipartBody .= "Content-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\n";
        $multipartBody .= "Content-Type: " . ($mimeType ?: mime_content_type($fileName)) . "\r\n\r\n";
        $multipartBody .= $fileBody . "\r\n";
        $multipartBody .= "--$boundary--\r\n";

        $headers['Content-Type'] = "multipart/form-data; boundary=$boundary";

        return self::sendRequest(new Request('POST', $url, $headers, $multipartBody));
    }
}
