<?php

class GodaddyDns {
    private $accessKeyId;
    private $accessSecret;
    private $DomainName;
    private $Host = '';
    private $Path = '';

    public function __construct($accessKeyId, $accessSecret, $domain = "") {
        $this->accessKeyId = $accessKeyId;
        $this->accessSecret = $accessSecret;
        if (!empty($domain)) {
            list($this->DomainName, $this->Host, $this->Path) = self::getDomain($domain);
        }
    }

    public static function getDomain($domain) {
        $parts = explode('.', $domain, 3);
        return array(
            isset($parts[2]) ? implode('.', array_slice($parts, 1)) : '',
            isset($parts[0]) ? $parts[0] : '',
            isset($parts[1]) ? $parts[1] : ''
        );
    }

    private function curl($url, $header = [], $data = '', $method = 'GET') {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
