            'oauth_consumer_key' => $consumerKey,
            'oauth_nonce' => time(),
            'oauth_signature_method' => 'HMAC-SHA1',
            'oauth_timestamp' => time(),
            'oauth_token' => $accessToken,
            'oauth_version' => '1.0'
        ];

        $keys = array_merge(array_keys($oauth), array_keys($this->postfields));
        sort($keys);

        $signatureData = '';
        foreach ($keys as $key) {
            $signatureData .= $key . '=' . rawurlencode($oauth[$key]) . '&';
        }
        $signatureData = substr($signatureData, 0, -1);
        $signatureData .= '&' . rawurlencode($consumerSecret) . '&' . rawurlencode(http_build_query($this->postfields));

        $oauth['oauth_signature'] = base64_encode(hash_hmac('sha1', $signatureData, $consumerSecret . '&' . $accessTokenSecret, true));

        $this->oauth = $oauth;
        $this->url = $url;
        return $this;
    }

