<?php
class TwitterAPIExchange {
    private $oauth_access_token;
    private $oauth_access_token_secret;
    private $consumer_key;
    private $consumer_secret;
    private $postfields = [];
    private $getfield = null;
    private $oauth = [];
    private $url;

    public function __construct(array $settings) {
        if (!isset($settings['oauth_access_token']) || !isset($settings['oauth_access_token_secret']) ||
            !isset($settings['consumer_key']) || !isset($settings['consumer_secret'])) {
            throw new Exception('Missing required settings');
        }
        $this->oauth_access_token = $settings['oauth_access_token'];
        $this->oauth_access_token_secret = $settings['oauth_access_token_secret'];
        $this->consumer_key = $settings['consumer_key'];
        $this->consumer_secret = $settings['consumer_secret'];
    }

    public function setPostfields(array $array): self {
        $this->postfields = $array;
        return $this;
    }

    public function setGetfield(string $string): self {
        $this->getfield = $string;
        return $this;
    }

    public function getGetfield(): ?string {
        return $this->getfield;
    }

    public function getPostfields(): ?array {
        return $this->postfields;
    }

    public function buildOauth(string $url, string $requestMethod): self {
        $this->url = $url;
        $this->oauth = [
            'oauth_consumer_key' => $this->consumer_key,
            'oauth_nonce' => time(),
            'oauth_signature_method' => 'HMAC-SHA1',
            'oauth_timestamp' => time(),
            'oauth_token' => $this->oauth_access_token,
            'oauth_version' => '1.0'
        ];

        $base_string = $this->buildBaseString($url, $requestMethod);
        $signature = base64_encode(hash_hmac('sha1', $base_string, $this->consumer_secret . '&' . $this->oauth_access_token_secret, true));
        $this->oauth['oauth_signature'] = $signature;

        return $this;
    }

    public function performRequest(bool $return = true) {
        $header = ['Expect:'];
        $oauth_header = $this->buildAuthorizationHeader($this->oauth);
        array_push($header, $oauth_header);

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $this->url . ($this->getfield ? '?' . $this->getfield : ''));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $header);

        if ($this->postfields) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($this->postfields));
        }

        $response = curl_exec($ch);
        if ($return) {
            return $response;
        } else {
            echo $response;
        }
    }

    private function buildBaseString(string $baseURI, string $method): string {
        $parts = parse_url($baseURI);
        $query = isset($parts['query']) ? $parts['query'] : '';
        parse_str($query, $query_array);
        ksort($query_array);
        $encoded_query = http_build_query($query_array, '', '&', PHP_QUERY_RFC3986);
        return strtoupper($method) . '&' . urlencode($baseURI) . '&' . urlencode($encoded_query);
    }

    private function buildAuthorizationHeader(array $oauth): string {
        $header_parts = [];
        foreach ($oauth as $key => $value) {
            $header_parts[] = $key . '="' . rawurlencode($value) . '"';
        }
        return 'OAuth ' . implode(', ', $header_parts);
    }
}
