class Request {
    public $method;
    public $url;
    public $body;
    public $headers;

    public function __construct($method, $url, $body = null, $headers = []) {
        $this->method = $method;
        $this->url = $url;
        $this->body = $body;
        $this->headers = $headers;
    }
}

class HttpClient {
    private static function userAgent() {
        return sprintf('SDK/1.0 System/%s PHP/%s', php_uname('n'), PHP_VERSION);
    }

    private static function parseHeaders($raw) {
        $headers = [];
        foreach (explode("\r\n", $raw) as $header) {
            if (strpos($header, ':') !== false) {
                list($key, $value) = explode(':', $header, 2);
                $headers[str_replace('-', '_', strtolower(trim($key)))] = trim($value);
            }
        }
        return $headers;
    }

    private static function escapeQuotes($str) {
        return str_replace('"', '\\"', $str);
    }

    private static function ucwordsHyphen($str) {
        return ucwords(str_replace('-', ' ', $str));
    }

    private static function sendRequest(Request $request) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $request->url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->method);

        if ($request->method === 'POST' || $request->method === 'PUT') {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
        }

        if (!empty($request->headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $request->headers);
        }

        $startTime = microtime(true);
        $response = curl_exec($ch);
        $endTime = microtime(true);

        if ($response === false) {
            throw new Exception(curl_error($ch));
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $body = substr($response, $headerSize);
        $headers = self::parseHeaders(substr($response, 0, $headerSize));

        curl_close($ch);

        return new Response($httpCode, $headers, $body, ['duration' => $endTime - $startTime]);
    }

    public static function get($url, $headers = []) {
        return self::sendRequest(new Request('GET', $url, null, $headers));
    }

    public static function delete($url, $headers = []) {
        return self::sendRequest(new Request('DELETE', $url, null, $headers));
    }

    public static function post($url, $body, $headers = []) {
        return self::sendRequest(new Request('POST', $url, $body, $headers));
    }

    public static function put($url, $body, $headers = []) {
        return self::sendRequest(new Request('PUT', $url, $body, $headers));
    }

    public static function multipartPost($url, $fields, $name, $fileName, $fileBody, $mimeType = null, $headers = []) {
        $boundary = uniqid();
        $multipartData = '';

        foreach ($fields as $key => $value) {
            $multipartData .= "--$boundary\r\n";
            $multipartData .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
            $multipartData .= $value . "\r\n";
        }

        $multipartData .= "--$boundary\r\n";
        $multipartData .= "Content-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\n";
        $multipartData .= "Content-Type: " . ($mimeType ?: 'application/octet-stream') . "\r\n\r\n";
        $multipartData .= $fileBody . "\r\n";
        $multipartData .= "--$boundary--\r\n";

        $headers[] = "Content-Type: multipart/form-data; boundary=$boundary";

        return self::post($url, $multipartData, $headers);
    }
}
