        if (strpos($headers['Content-Encoding'], 'gzip') !== false) {
            $saveFile .= '.gz';
        }

        if (isset($headers['Accept-Ranges']) && $headers['Accept-Ranges'] === 'bytes') {
            $result = self::fileDownloadCurl($url[0], $saveFile, true);
        } else {
            $result = self::fileDownloadFopen($url[0], $saveFile);
        }

        if ($result['success']) {
            self::checkGzip($saveFile);
        }

        return $result;
    }

    private static function fileDownloadFopen($url, $fileName, $headerSize = 0) {
        $fp = fopen($url, 'r');
        if (!$fp) {
            return ['success' => false, 'error' => 'Failed to open URL'];
        }

        $fileHandle = fopen($fileName, 'w');
        if (!$fileHandle) {
            fclose($fp);
            return ['success' => false, 'error' => 'Failed to create save file'];
        }

        while (!feof($fp)) {
            fwrite($fileHandle, fread($fp, 8192));
        }

        fclose($fileHandle);
        fclose($fp);

        return ['success' => true];
    }

    private static function fileDownloadCurl($url, $fileName, $supportRange = false, $existsLength = 0, $length = 0) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);

        if ($supportRange && $existsLength > 0) {
            curl_setopt($ch, CURLOPT_RANGE, "$existsLength-$length");
        }

        $data = curl_exec($ch);
        if ($data === false) {
            curl_close($ch);
            return ['success' => false, 'error' => 'cURL Error: ' . curl_error($ch)];
        }

        $fileHandle = fopen($fileName, 'a');
        if (!$fileHandle) {
            curl_close($ch);
            return ['success' => false, 'error' => 'Failed to create save file'];
        }

        fwrite($fileHandle, $data);
        fclose($fileHandle);
        curl_close($ch);

        return ['success' => true];
    }

    private static function checkGzip($file) {
        if (substr($file, -3) === '.gz') {
            $content = gzdecode(file_get_contents($file));
            unlink($file);
            file_put_contents($file, $content);
        }
    }
