class Bcs {
    private $rootPath;
    private $error;
    private $config = [];
    private $bcs;

    public function __construct(array $config) {
        $this->config = $config;
        $this->rootPath = isset($config['rootPath']) ? $config['rootPath'] : '';
        $this->bcs = new BaiduBCS($config['AccessKey'], $config['SecretKey']);
    }

    public function request(string $path, string $method, array $headers = null, mixed $body = null): mixed {
        try {
            $url = $this->rootPath . '/' . ltrim($path, '/');
            $ch = curl_init();
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_TIMEOUT, isset($this->config['timeout']) ? $this->config['timeout'] : 30);

            if ($headers !== null) {
                curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
            }

            if ($method === 'POST' || $method === 'PUT') {
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
                curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
            } elseif ($method === 'DELETE') {
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
            }

            $response = curl_exec($ch);
            if ($response === false) {
                throw new Exception(curl_error($ch));
            }

            return $this->response($response);
        } catch (Exception $e) {
            $this->error = $e->getMessage();
            return null;
        } finally {
            curl_close($ch);
        }
    }

    public function response(string $text): array {
        return json_decode($text, true);
    }

    public function error(string $header): void {
        if (strpos($header, 'HTTP/1.1 40') === 0) {
            $this->error = 'Not Found';
        } elseif (strpos($header, 'HTTP/1.1 50') === 0) {
            $this->error = 'Server Error';
