        $this->success = false;
        $this->headers = [];
        $this->body = '';
        $this->status_code = 0;
        $this->redirects = 0;
        $this->error = '';
        $this->method = CURLINFO_EFFECTIVE_URL;
        $this->permanent_url = $url;
        $this->permanentUrlMutable = true;

        $ch = curl_init();
        curl_setopt_array($ch, [
            CURLOPT_URL => $url,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_TIMEOUT => $timeout,
            CURLOPT_FOLLOWLOCATION => false,
            CURLOPT_USERAGENT => $this->useragent,
            CURLOPT_HEADER => true,
            ...$curl_options
        ]);

        if ($force_fsockopen) {
            curl_setopt($ch, CURLOPT_FTP_USE_EPRT, false);
            curl_setopt($ch, CURLOPT_FTP_USE_EPSV, false);
        }

        $response = curl_exec($ch);

        if ($response === false) {
            $this->error = curl_error($ch);
        } else {
            $header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
            $this->headers = explode("\r\n", substr($response, 0, $header_size));
            $this->body = substr($response, $header_size);
            $this->status_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            $this->success = $this->status_code >= 200 && $this->status_code < 300;
            $this->url = curl_getinfo($ch, CURLINFO_EFFECTIVE_URL);
            $this->redirects = curl_getinfo($ch, CURLINFO_REDIRECT_COUNT);
        }

