            new Qiniu_Response($httpCode, substr($responseBody, strlen(http_build_query($responseHeaders))), $responseHeaders),
            null
        ];
    }

    public function getReqInfo($headerContent) {
        preg_match('/X-Reqid:\s*(.*?)\s*/', $headerContent, $matches);
        $reqid = isset($matches[1]) ? $matches[1] : '';

        preg_match('/X-Log:\s*(.*?)\s*/', $headerContent, $matches);
        $xLog = isset($matches[1]) ? $matches[1] : '';

        return ['reqid' => $reqid, 'xLog' => $xLog];
    }
