<?php
function serveFavicon($url, $cache_time, $default_ico, $basename = "favicon.ico") {
    $cache_dir = __DIR__ . '/cache';
    if (!is_dir($cache_dir)) {
        mkdir($cache_dir, 0777, true);
    }

    $cache_file = $cache_dir . '/' . md5($url . $basename);
    $current_time = time();

    if (file_exists($cache_file) && ($current_time - filemtime($cache_file) < $cache_time)) {
        header('Content-Type: image/x-icon');
        readfile($cache_file);
        exit;
    }

    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url . '/' . $basename);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_TIMEOUT, 10);
    $response = curl_exec($ch);
    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    curl_close($ch);

    if ($http_code == 200 && exif_imagetype($response) !== false) {
        file_put_contents($cache_file, $response);
        header('Content-Type: image/x-icon');
        echo $response;
    } else {
        header('Content-Type: image/x-icon');
        readfile($default_ico);
    }
}
