<?php
function serveFavicon($url, $cache_time, $default_ico, $basename = "favicon.ico") {
    $cache_dir = __DIR__ . '/cache/';
    if (!is_dir($cache_dir)) {
        mkdir($cache_dir, 0755, true);
    }

    $cache_file = $cache_dir . md5($url . $basename) . '.ico';
    $last_modified = @filemtime($cache_file);
    $etag = @md5_file($cache_file);

    header('Content-Type: image/x-icon');
    header('Cache-Control: max-age=' . $cache_time . ', public');
    header('Last-Modified: ' . gmdate('D, d M Y H:i:s \G\M\T', $last_modified));
    header('ETag: "' . $etag . '"');

    if (@strtotime($_SERVER['HTTP_IF_MODIFIED_SINCE']) >= $last_modified && isset($_SERVER['HTTP_IF_NONE_MATCH']) && $_SERVER['HTTP_IF_NONE_MATCH'] == '"' . $etag . '"') {
        http_response_code(304);
        exit;
    }

    if (file_exists($cache_file)) {
        readfile($cache_file);
        exit;
    }

    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url . '/' . $basename);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_TIMEOUT, 10);
    curl_setopt($ch, CURLOPT_HEADER, false);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
    $response = curl_exec($ch);
    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    curl_close($ch);

    if ($http_code == 200 && preg_match('/image\/x?icon/i', curl_getinfo($ch, CURLINFO_CONTENT_TYPE))) {
        file_put_contents($cache_file, $response);
        echo $response;
    } else {
        header('Location: ' . $default_ico);
        exit;
    }
}
?>
