    private $backendToken;

    public function __construct() {
        $this->backendUrl = getenv('BACKEND_URL');
        $this->backendToken = getenv('BACKEND_TOKEN');
    }

    public function addTask($id) {
        return $this->setCurl($this->backendUrl, $this->backendToken, 'add', $id);
    }

    public function restartTask($id) {
        return $this->setCurl($this->backendUrl, $this->backendToken, 'restart', $id);
    }

    public function removeTask($id) {
        return $this->setCurl($this->backendUrl, $this->backendToken, 'remove', $id);
    }

    private function setCurl($backendUrl, $backendToken, $operation, $id) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, "$backendUrl/$operation/$id");
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            "Authorization: Bearer $backendToken",
            "Content-Type: application/json"
        ]);

        $response = curl_exec($ch);
        if ($response === false) {
            return ['status' => 'error', 'message' => curl_error($ch)];
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode == 200) {
            return ['status' => 'success', 'message' => 'Operation successful'];
        } else {
            return ['status' => 'error', 'message' => "HTTP Error: $httpCode"];
        }
    }
}
