class BackendService {
    private static $backendUrl;
    private static $backendToken;

    public function __construct() {
        self::$backendUrl = getenv('BACKEND_URL');
        self::$backendToken = getenv('BACKEND_TOKEN');
    }

    public function addTask($id) {
        return $this->setCurl(self::$backendUrl . '/tasks', self::$backendToken, 'POST', $id);
    }

    public function restartTask($id) {
        return $this->setCurl(self::$backendUrl . '/tasks/' . $id . '/restart', self::$backendToken, 'PUT', $id);
    }

    public function removeTask($id) {
        return $this->setCurl(self::$backendUrl . '/tasks/' . $id, self::$backendToken, 'DELETE', $id);
    }

    private function setCurl($url, $token, $operation, $id) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $operation);
        if ($operation === 'POST' || $operation === 'PUT') {
            curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode(['id' => $id]));
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                'Content-Type: application/json',
                'Authorization: Bearer ' . $token
            ]);
        } else {
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                'Authorization: Bearer ' . $token
            ]);
        }
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode == 200) {
            return ['status' => 'success', 'message' => 'Operation successful'];
        } else {
            return ['status' => 'error', 'message' => 'Operation failed with HTTP code ' . $httpCode];
        }
    }
}
