    private function parse_headers($headers) {
        $parsedHeaders = [];
        foreach (explode("\n", $headers) as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $parsedHeaders[trim($key)] = trim($value);
            }
        }
        return $parsedHeaders;
