class Pretreat
{
    protected $config;

    public function __construct(Config $config)
    {
        if (!$config->processNotifyUrl) {
            throw new \Exception("should config prosessNotifyUrl first.");
        }
        $this->config = $config;
    }

    public function process($tasks, $optionalParams = array())
    {
        $encodedTasks = Util::base64Json($tasks);

        $client = new Client([
            'timeout' => $this->config->timeout,
        ]);

        $params = array(
            'service' => $this->config->serviceName,
            'notify_url' => $this->config->processNotifyUrl,
            'tasks' => $encodedTasks,
        );

        $params = array_merge($params, $optionalParams);

        $path = '/pretreatment/';
        $method = 'POST';
        $signedHeaders = Signature::getHeaderSign($this->config, $method, $path);

        $url = $this->config->getPretreatEndPoint() . $path;
        $response = $client->request($method, $url, [
            'headers' => $signedHeaders,
            'form_params' => $params
        ]);

        $body = $response->getBody()->getContents();
        return json_decode($body, true);
    }

    public function query($taskIds, $path)
    {
        $client = new Client([
            'timeout' => $this->config->timeout,
        ]);

        $params = array(
            'service' => $this->config->serviceName,
            'task_ids' => implode(',', $taskIds)
        );
        $path = $path . '?' . http_build_query($params);

        $method = 'GET';
        $url = $this->config->getPretreatEndPoint() . $path;
        $signedHeaders = Signature::getHeaderSign($this->config, $method, $path);
        $response = $client->request($method, $url, [
            'headers' => $signedHeaders
        ]);

        if ($response->getStatusCode() === 200) {
            $body = $response->getBody()->getContents();
            $result = json_decode($body, true);
            if (is_array($result)) {
                return $result['tasks'];
            }
        }
        return false;
    }
}

use GuzzleHttp\Client;
use Exception;

class FetchConversionRate
{
    protected $baseCurrencyId;
    private $currencyRepository;
    private $conversionRateRepository;

    public function __construct(int $baseCurrencyId)
    {
        $this->baseCurrencyId = $baseCurrencyId;
    }

    public function handle(
        CurrencyRepository $currencyRepository,
        ConversionRateRepository $conversionRateRepository
    ): void {
        $this->currencyRepository = $currencyRepository;
        $this->conversionRateRepository = $conversionRateRepository;

        $baseCurrency = $this->currencyRepository->getById($this->baseCurrencyId);

        if (!$baseCurrency || !$baseCurrency->iso) {
            return;
        }

        $client = new Client();

        foreach ($this->currencyRepository->getAll() as $targetCurrency) {
            if (!$targetCurrency->iso || $baseCurrency->iso === $targetCurrency->iso) {
                continue;
            }

            $url = sprintf(
                'https://cdn.jsdelivr.net/npm/@fawazahmed0/currency-api@latest/v1/currencies/%s.json',
                $baseCurrency->iso_lowercased,
            );

            try {
                $response = $client->request('GET', $url);
                $decoded_response = json_decode($response->getBody(), true);
                $rate = $decoded_response[$baseCurrency->iso_lowercased][$targetCurrency->iso_lowercased];
            } catch (Exception) {
                continue;
            }

            $this->conversionRateRepository->createOrUpdate(
                $baseCurrency->id,
                $targetCurrency->id,
                $rate
            );
        }
    }
}

