class Picture {
    private $compressLimit;
    private $sizeLimit;

    public function __construct(int $compressLimit, int $sizeLimit) {
        $this->compressLimit = $compressLimit;
        $this->sizeLimit = $sizeLimit;
    }

    public function downloadImage(string $url): string {
        $ch = curl_init();
        $content = '';
        $downloadSize = 0;
        $contentType = '';

        curl_setopt_array($ch, [
            CURLOPT_URL => $url,
            CURLOPT_RETURNTRANSFER => false,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_HEADER => false,
            CURLOPT_WRITEFUNCTION => function ($curl, $data) use (&$content, &$downloadSize) {
                $length = strlen($data);
                $downloadSize += $length;
                if ($this->compressLimit > 0 && $downloadSize > $this->compressLimit) {
                    return 0;
                }
                $content .= $data;
                return $length;
            },
            CURLOPT_HEADERFUNCTION => function ($curl, $header) use (&$contentType) {
                if (stripos($header, 'Content-Type:') === 0) {
                    $parts = explode(':', $header, 2);
                    $contentType = trim($parts[1]);
                }
                return strlen($header);
            }
        ]);

        $success = curl_exec($ch);
        $curlError = curl_error($ch);
        curl_close($ch);

        if ($curlError) {
            throw new \Exception("cURL error: $curlError");
        }

        if ($this->compressLimit > 0 && $downloadSize > $this->compressLimit) {
            throw new \Exception("Downloaded data exceeds compression limit of {$this->compressLimit} bytes.");
        }

        $imageResource = @imagecreatefromstring($content);
        if (!$imageResource) {
            throw new \Exception("Failed to create image from data.");
        }

        $width = imagesx($imageResource);
        $height = imagesy($imageResource);
        imagedestroy($imageResource);

        if ($width > $this->sizeLimit || $height > $this->sizeLimit) {
            throw new \Exception("Image dimensions exceed size limit of {$this->sizeLimit} pixels.");
        }

        return $content;
    }
}

