        $this->url = $url;
        $parts = parse_url($url);
        $this->scheme = $parts['scheme'];
        $this->host = $parts['host'];
        $this->port = isset($parts['port']) ? $parts['port'] : ($this->scheme === 'https' ? 443 : 80);
        $this->path = isset($parts['path']) ? $parts['path'] : '/';
        if (isset($parts['query'])) {
            $this->path .= '?' . $parts['query'];
        }
        $this->uri = $this->path;

        if ($use_curl) {
            $this->use_curl = true;
            $this->ch = curl_init();
            if (is_array($curl_options)) {
                foreach ($curl_options as $option => $value) {
                    $this->setCurlOption($option, $value);
                }
            }
        }
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            return true;
        }
        if ($this->fp) {
            return true;
        }
        $errno = 0;
        $errstr = '';
        $timeout = $connection_timeout ?: 30;
