            $proxy_auth = '';
            if (isset($this->proxy['username'])) {
                $auth = base64_encode($this->proxy['username'] . ':' . $this->proxy['password']);
                $proxy_auth = "Proxy-Authorization: Basic $auth\r\n";
            }
            fwrite($this->fp, "CONNECT {$this->host}:{$this->port} HTTP/1.1\r\n");
            fwrite($this->fp, "Host: {$this->host}:{$this->port}\r\n");
            fwrite($this->fp, $proxy_auth);
            fwrite($this->fp, "\r\n");

