                curl_setopt($this->ch, CURLOPT_PROXY, $this->proxy['host']);
                curl_setopt($this->ch, CURLOPT_PROXYPORT, $this->proxy['port']);
                if (isset($this->proxy['username'])) {
                    curl_setopt($this->ch, CURLOPT_PROXYUSERPWD, $this->proxy['username'] . ':' . $this->proxy['password']);
                }
            }

            return true;
        } else {
            $default_headers = [
                'Host' => $this->host,
                'Content-Type' => 'text/xml; charset=utf-8',
                'Content-Length' => strlen($data),
                'Connection' => $this->persistentConnection ? 'Keep-Alive' : 'Close'
            ];
            $headers = array_merge($default_headers, $this->outgoing_headers);

            $request_line = "{$this->request_method} {$this->uri} HTTP/{$this->protocol_version}\r\n";
            $header_str = '';
            foreach ($headers as $name => $value) {
                $header_str .= "$name: $value\r\n";
            }

            if ($this->proxy && $this->scheme === 'http') {
                $request_line = "{$this->request_method} {$this->url} HTTP/{$this->protocol_version}\r\n";
