    public function getResponse() {
        if ($this->use_curl) {
            $response = curl_exec($this->ch);
            if ($response === false) {
                return false;
            }
            $header_size = curl_getinfo($this->ch, CURLINFO_HEADER_SIZE);
            $headers = substr($response, 0, $header_size);
            $body = substr($response, $header_size);
