            $this->incoming_headers = $this->parseHeaders($headers);
            return $body;
        } else {
            $headers = '';
            $timed_out = false;
            stream_set_blocking($this->fp, true);
            while (!feof($this->fp) && !$timed_out) {
                $line = fgets($this->fp);
                if ($line === "\r\n") break;
                if ($line === false) {
                    $timed_out = true;
                    break;
                }
                $headers .= $line;
            }
            if ($timed_out) {
                return false;
            }
            $this->incoming_headers = $this->parseHeaders($headers);
            $length = isset($this->incoming_headers['Content-Length']) ? (int)$this->incoming_headers['Content-Length'] : 0;
            $body = '';
            if ($length > 0) {
                $body = fread($this->fp, $length);
            } else {
                $body = stream_get_contents($this->fp);
            }
            $this->incoming_payload = $body;
            if (!$this->persistentConnection) {
                fclose($this->fp);
                $this->fp = null;
            }
            return $body;
        }
    }

    public function setHeader($name, $value) {
        $this->outgoing_headers[$name] = $value;
    }

    public function setCurlOption($option, $value) {
        if ($this->use_curl) {
            curl_setopt($this->ch, $option, $value);
        }
    }

    private function parseHeaders($header_str) {
        $headers = array();
        $lines = explode("\n", $header_str);
        $first = array_shift($lines);
        foreach ($lines as $line) {
            if (trim($line) === '') continue;
            if (strpos($line, ':') === false) continue;
            list($name, $value) = explode(':', trim($line), 2);
            $name = trim($name);
            $value = trim($value);
            if (strtolower($name) === 'set-cookie') {
                $parsed = $this->parseCookie($value);
                if ($parsed) {
                    if (!isset($headers['Set-Cookie'])) {
                        $headers['Set-Cookie'] = array();
                    }
                    $headers['Set-Cookie'][] = $parsed;
                }
            } else {
                $headers[$name] = $value;
            }
        }
        return $headers;
    }

    public function parseCookie($cookie_str) {
        $parts = explode(';', $cookie_str);
        $cookie = array();
        $first = true;
        foreach ($parts as $part) {
            $pair = explode('=', trim($part), 2);
            if ($first && count($pair) >= 1) {
                $cookie['name'] = $pair[0];
                $cookie['value'] = $pair[1] ?? '';
                $first = false;
            } else if (count($pair) === 2) {
                $key = strtolower($pair[0]);
                $cookie[$key] = $pair[1];
            } else if (count($pair) === 1 && $pair[0] !== '') {
                $key = strtolower($pair[0]);
                $cookie[$key] = true;
            }
        }
        return $cookie;
    }
}

