    } else {
        $parts = parse_url($url);
        $scheme = isset($parts['scheme']) ? strtolower($parts['scheme']) : 'http';
        $host = $ip ? $ip : $parts['host'];
        $port = isset($parts['port']) ? $parts['port'] : ($scheme === 'https' ? 443 : 80);
        $path = isset($parts['path']) ? $parts['path'] : '/';
        if (isset($parts['query'])) {
            $path .= '?' . $parts['query'];
        }
        $fp = fsockopen(
            ($scheme === 'https' ? 'ssl://' : '') . $host,
            $port,
            $errno,
            $errstr,
            $timeout
        );
        if (!$fp) {
            return '';
        }
        stream_set_blocking($fp, $block);
        $out = '';
        if ($post === '' && empty($files)) {
            $out .= "GET $path HTTP/1.1\r\n";
        } else {
            $out .= "POST $path HTTP/1.1\r\n";
        }
        $out .= "Host: $host\r\n";
        if (!empty($cookie)) {
            $out .= "Cookie: $cookie\r\n";
        }
        $postBody = '';
        if ($post !== '' || !empty($files)) {
            if (!empty($files)) {
                $boundary = '---------------------' . substr(md5(rand(0, 32000)), 0, 10);
                $out .= "Content-Type: multipart/form-data; boundary=$boundary\r\n";
                $postBody = '';
                if (is_array($post)) {
                    foreach ($post as $key => $value) {
                        $postBody .= "--$boundary\r\n";
                        $postBody .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
                        $postBody .= "$value\r\n";
                    }
                }
                foreach ($files as $key => $filepath) {
                    $filename = basename($filepath);
                    $mimetype = mime_content_type($filepath) ?: 'application/octet-stream';
                    $postBody .= "--$boundary\r\n";
                    $postBody .= "Content-Disposition: form-data; name=\"$key\"; filename=\"$filename\"\r\n";
                    $postBody .= "Content-Type: $mimetype\r\n\r\n";
                    $filecontent = file_get_contents($filepath);
                    $postBody .= "$filecontent\r\n";
                }
                $postBody .= "--$boundary--\r\n";
            } elseif ($encodetype === 'URLENCODE') {
                $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
                if (is_array($post)) {
                    $postBody = http_build_query($post);
                } else {
                    $postBody = $post;
                }
            } else {
                $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
                $postBody = $post;
            }
            $out .= "Content-Length: " . strlen($postBody) . "\r\n";
        }
        $out .= "Connection: Close\r\n\r\n";
        $out .= $postBody;
        fwrite($fp, $out);
        $responseHeaders = [];
        while (!feof($fp)) {
            $line = fgets($fp, 4096);
            if ($line === "\r\n") {
                break;
            }
            $responseHeaders[] = trim($line);
        }
        $GLOBALS['filesockheader'] = $responseHeaders;
        $body = '';
        while (!feof($fp) && ($buf = fread($fp, 8192)) !== false) {
            $body .= $buf;
            if ($limit > 0 && strlen($body) >= $limit) {
                $body = substr($body, 0, $limit);
                break;
            }
        }
        fclose($fp);
        return $body;
    }
}

