class UdpScraper {
    private $currentConnectionId;
    private $transactionId;
    private $socket;
    private $timeout;

    public function scrape(Tracker $tracker, array $hashes) {
        $result = [];
        $chunks = array_chunk($hashes, 74);
        foreach ($chunks as $chunk) {
            $data = $this->scrapeChunk($tracker, $chunk);
            foreach ($data as $hash => $stats) {
                $result[$hash] = $stats;
            }
        }
        return $result;
    }

    private function scrapeChunk(Tracker $tracker, array $hashes) {
        $this->socket = $this->initSocket();
        $this->connect($tracker->getDomain(), $tracker->getPort());
        $this->startTransaction();
        $data = $this->scrapeHashes($hashes);
        $this->closeSocket();
        return $data;
    }

    private function initSocket() {
        $socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);
        if ($socket === false) {
            throw new Exception('UDP socket creation failed: ' . socket_strerror(socket_last_error()));
        }
        socket_set_option($socket, SOL_SOCKET, SO_RCVTIMEO, ['sec' => $this->timeout, 'usec' => 0]);
        socket_set_option($socket, SOL_SOCKET, SO_SNDTIMEO, ['sec' => $this->timeout, 'usec' => 0]);
        return $socket;
    }

    private function connect($domain, $port) {
        if (!socket_connect($this->socket, $domain, $port)) {
            throw new Exception('UDP socket connect failed: ' . socket_strerror(socket_last_error()));
        }
    }

    private function generateTransactionId() {
        $this->transactionId = mt_rand(0, 0xFFFFFFFF);
    }

    private function startTransaction() {
        $this->generateTransactionId();
        $packet = pack('J', 0x41727101980) . pack('N', 0) . pack('N', $this->transactionId);
        $response = $this->request($packet, 16);
        $this->checkError($response);
        $buffer = unpack('Naction/NtransId/JconnId', $response);
        if ($buffer['action'] !== 0 || $buffer['transId'] !== $this->transactionId) {
            throw new Exception('UDP connect invalid response');
        }
        $this->currentConnectionId = $buffer['connId'];
    }

    private function scrapeHashes(array $hashes) {
        $this->generateTransactionId();
        $packet = pack('J', $this->currentConnectionId) . pack('N', 2) . pack('N', $this->transactionId);
        foreach ($hashes as $hash) {
            $packet .= $hash;
        }
        $response = $this->request($packet, 16 + 12 * count($hashes));
        $this->checkError($response);
        $buffer = unpack('Naction/NtransId/JconnId', substr($response, 0, 16));
        if ($buffer['action'] !== 2 || $buffer['transId'] !== $this->transactionId || $buffer['connId'] !== $this->currentConnectionId) {
            throw new Exception('Invalid scrape response');
        }
        $data = [];
        for ($i = 0; $i < count($hashes); $i++) {
            $offset = 16 + $i * 12;
            if ($offset + 12 > strlen($response)) break;
            $entry = substr($response, $offset, 12);
            list($seeders, $completed, $leechers) = unpack('N3', $entry);
            $data[$hashes[$i]] = [$seeders, $completed, $leechers];
        }
        return $data;
    }

    private function request($packet, $readLength) {
        if (socket_send($this->socket, $packet, strlen($packet), 0) === false) {
            $this->closeSocket();
            throw new Exception('UDP send failed: ' . socket_strerror(socket_last_error()));
        }
        $response = '';
        $startTime = microtime(true);
        while (strlen($response) < $readLength) {
            $remaining = $readLength - strlen($response);
            $buf = socket_read($this->socket, $remaining);
            if ($buf === false) {
                $code = socket_last_error($this->socket);
                if ($code === SOCKET_EAGAIN || $code === SOCKET_EWOULDBLOCK) {
                    if ((microtime(true) - $startTime) > $this->timeout) {
                        $this->closeSocket();
                        throw new Exception('UDP request timeout');
                    }
                    continue;
                }
                $this->closeSocket();
                throw new Exception('UDP read error: ' . socket_strerror($code));
            }
            if ($buf === '') break;
            $response .= $buf;
        }
        if (strlen($response) < $readLength) {
            $this->closeSocket();
            throw new Exception('UDP incomplete response');
        }
        return $response;
    }

    private function closeSocket() {
        if ($this->socket) {
            socket_close($this->socket);
            $this->socket = null;
        }
        $this->currentConnectionId = null;
    }

    private function checkError($response) {
        if (strlen($response) >= 8) {
            $buffer = unpack('Naction/NtransId', $response);
            if ($buffer['action'] === 3) {
                $errorMsg = substr($response, 8);
                $this->closeSocket();
                throw new Exception('UDP Tracker Error: ' . $errorMsg);
            }
        }
    }
}

