use Symfony\Component\HttpClient\HttpClient;
use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\HttpFoundation\ResponseHeaderBag;
use Symfony\Component\Mime\Part\AbstractPart;
use Symfony\Component\Mime\Part\DataPart;
use Symfony\Component\Mime\Part\Multipart\FormDataPart;
use Symfony\Component\Mime\Part\TextPart;
use Symfony\Contracts\HttpClient\HttpClientInterface;

class HttpClientKernel implements HttpKernelInterface
{
    private HttpClientInterface $client;

    public function __construct(HttpClientInterface $client = null)
    {
        if (null === $client && !class_exists(HttpClient::class)) {
            throw new \LogicException(sprintf('You cannot use "%s" as the HttpClient component is not installed. Try running "composer require symfony/http-client".', __CLASS__));
        }

        $this->client = $client ?? HttpClient::create();
    }

    public function handle(Request $request, int $type = HttpKernelInterface::MAIN_REQUEST, bool $catch = true): Response
    {
        $headers = $this->getHeaders($request);
        $body = '';
        if (null !== $part = $this->getBody($request)) {
            $headers = array_merge($headers, $part->getPreparedHeaders()->toArray());
            $body = $part->bodyToIterable();
        }
        $response = $this->client->request($request->getMethod(), $request->getUri(), [
            'headers' => $headers,
            'body' => $body,
        ] + $request->attributes->get('http_client_options', []));

        $response = new Response($response->getContent(!$catch), $response->getStatusCode(), $response->getHeaders(!$catch));

        $response->headers->remove('X-Body-File');
        $response->headers->remove('X-Body-Eval');
        $response->headers->remove('X-Content-Digest');

        $response->headers = new class($response->headers->all()) extends ResponseHeaderBag {
            protected function computeCacheControlValue(): string
            {
                return $this->getCacheControlHeader();
            }
        };

        return $response;
    }

    private function getBody(Request $request): ?AbstractPart
    {
        if (\in_array($request->getMethod(), ['GET', 'HEAD'])) {
            return null;
        }

        if (!class_exists(AbstractPart::class)) {
            throw new \LogicException('You cannot pass non-empty bodies as the Mime component is not installed. Try running "composer require symfony/mime".');
        }

        if ($content = $request->getContent()) {
            return new TextPart($content, 'utf-8', 'plain', '8bit');
        }

        $fields = $request->request->all();
        foreach ($request->files->all() as $name => $file) {
            $fields[$name] = DataPart::fromPath($file->getPathname(), $file->getClientOriginalName(), $file->getClientMimeType());
        }

        return new FormDataPart($fields);
    }

    private function getHeaders(Request $request): array
    {
        $headers = [];
        foreach ($request->headers as $key => $value) {
            $headers[$key] = $value;
        }
        $cookies = [];
        foreach ($request->cookies->all() as $name => $value) {
            $cookies[] = $name.'='.$value;
        }
        if ($cookies) {
            $headers['cookie'] = implode('; ', $cookies);
        }

        return $headers;
    }
}

namespace WP_CLI;

use RuntimeException;

class Process {
	private $command;
	private $cwd;
	private $env;
	private static $descriptors = [
		0 => STDIN,
		1 => [ 'pipe', 'w' ],
		2 => [ 'pipe', 'w' ],
	];
	public static $log_run_times = false;
	public static $run_times = [];

	public static function create( $command, $cwd = null, $env = [] ) {
		$proc = new self();
		$proc->command = $command;
		$proc->cwd = $cwd;
		$proc->env = $env;
		return $proc;
	}

	private function __construct() {}

	public function run() {
		Utils\check_proc_available( 'Process::run' );

		$start_time = microtime( true );
		$pipes = [];
		$proc = Utils\proc_open_compat( $this->command, self::$descriptors, $pipes, $this->cwd, $this->env );

		$stdout = stream_get_contents( $pipes[1] );
		fclose( $pipes[1] );

		$stderr = stream_get_contents( $pipes[2] );
		fclose( $pipes[2] );

		$return_code = proc_close( $proc );

		$run_time = microtime( true ) - $start_time;

		if ( self::$log_run_times ) {
			if ( ! isset( self::$run_times[ $this->command ] ) ) {
				self::$run_times[ $this->command ] = [ 0, 0 ];
			}
			self::$run_times[ $this->command ][0] += $run_time;
			++self::$run_times[ $this->command ][1];
		}

		return new ProcessRun( [
			'stdout' => $stdout,
			'stderr' => $stderr,
			'return_code' => $return_code,
			'command' => $this->command,
			'cwd' => $this->cwd,
			'env' => $this->env,
			'run_time' => $run_time,
		] );
	}

	public function run_check() {
		$r = $this->run();
		if ( $r->return_code ) {
			throw new RuntimeException( $r );
		}
		return $r;
	}

	public function run_check_stderr() {
		$r = $this->run();
		if ( $r->return_code ) {
			throw new RuntimeException( $r );
		}
		if ( ! empty( $r->stderr ) ) {
			$stderr_lines = array_filter( explode( "\n", $r->stderr ) );
			if ( 1 === count( $stderr_lines ) ) {
				$stderr_line = $stderr_lines[0];
				if ( false !== strpos( $stderr_line, 'The PSR-0 `Requests_...` class names in the Request library are deprecated.' ) ) {
					return $r;
				}
			}
			throw new RuntimeException( $r );
		}
		return $r;
	}
}

namespace EE;

use EE\Utils;

class Process {
	private $command;
	private $cwd;
	private $env;
	private static $descriptors = array(
		0 => STDIN,
		1 => array( 'pipe', 'w' ),
		2 => array( 'pipe', 'w' ),
	);
	public static $log_run_times = false;
	public static $run_times = array();

	public static function create( $command, $cwd = null, $env = array() ) {
		$proc = new self;
		$proc->command = $command;
		$proc->cwd = $cwd;
		$proc->env = $env;
		return $proc;
	}

	private function __construct() {}

	public function run() {
		$start_time = microtime( true );
		$proc = Utils\proc_open_compat( $this->command, self::$descriptors, $pipes, $this->cwd, $this->env );

		$stdout = stream_get_contents( $pipes[1] );
		fclose( $pipes[1] );

		$stderr = stream_get_contents( $pipes[2] );
		fclose( $pipes[2] );

		$return_code = proc_close( $proc );
		$run_time = microtime( true ) - $start_time;

		if ( self::$log_run_times ) {
			if ( ! isset( self::$run_times[ $this->command ] ) ) {
				self::$run_times[ $this->command ] = array( 0, 0 );
			}
			self::$run_times[ $this->command ][0] += $run_time;
			self::$run_times[ $this->command ][1]++;
		}

		return new ProcessRun( array(
			'stdout' => $stdout,
			'stderr' => $stderr,
			'return_code' => $return_code,
			'command' => $this->command,
			'cwd' => $this->cwd,
			'env' => $this->env,
			'run_time' => $run_time,
		) );
	}

	public function run_check() {
		$r = $this->run();
		if ( $r->return_code || ! empty( $r->STDERR ) ) {
			throw new \RuntimeException( $r );
		}
		return $r;
	}

	public function run_check_stderr() {
		$r = $this->run();
		if ( $r->return_code || ! empty( $r->stderr ) ) {
			throw new \RuntimeException( $r );
		}
		return $r;
	}
}

class Command
{
    public $escapeArgs = true;
    public $escapeCommand = false;
    public $useExec = false;
    public $captureStdErr = true;
    public $procCwd;
    public $procEnv;
    public $procOptions;

    protected $_command;
    protected $_args = array();
    protected $_execCommand;
    protected $_stdOut = '';
    protected $_stdErr = '';
    protected $_exitCode;
    protected $_error = '';
    protected $_executed = false;

    public function __construct($options = null)
    {
        if (is_array($options)) {
            $this->setOptions($options);
        } elseif (is_string($options)) {
            $this->setCommand($options);
        }
    }

    public function setCommand($command)
    {
        $this->_command = $this->escapeCommand ? escapeshellcmd($command) : $command;
        return $this;
    }

    public function getCommand()
    {
        return $this->_command;
    }

    public function getExecCommand()
    {
        if ($this->_execCommand===null) {
            $command = $this->getCommand();
            if (!$command) {
                $this->_error = 'Could not locate any executable command';
                return false;
            }
            $args = $this->getArgs();
            $this->_execCommand = $args ? $command.' '.$args : $command;
        }
        return $this->_execCommand;
    }

    public function addArg($key, $value = null, $escape = null)
    {
        $doEscape = $escape!==null ? $escape : $this->escapeArgs;
        if ($value===null) {
            $this->_args[] = $escape ? escapeshellarg($key) : $key;
        } else {
            $separator = substr($key, -1)==='=' ? '' : ' ';
            if (is_array($value)) {
                $params = array();
                foreach ($value as $v) {
                    $params[] = $doEscape ? escapeshellarg($v) : $v;
                }
                $this->_args[] = $key.$separator.implode(' ',$params);
            } else {
                $this->_args[] = $key.$separator.($doEscape ? escapeshellarg($value) : $value);
            }
        }
        return $this;
    }

    public function execute()
    {
        $command = $this->getExecCommand();

        if (!$command) {
            return false;
        }

        if ($this->useExec) {
            $execCommand = $this->captureStdErr ? "$command 2>&1" : $command;
            exec($execCommand, $output, $this->_exitCode);
            $this->_stdOut = trim(implode("\n", $output));
            if ($this->_exitCode!==0) {
                $this->_stdErr = $this->_stdOut;
                $this->_error = empty($this->_stdErr) ? 'Command failed' : $this->_stdErr;
                return false;
            }
        } else {
            $descriptors = array(
                1 => array('pipe','w'),
                2 => array('pipe','a'),
            );
            $process = proc_open($command, $descriptors, $pipes, $this->procCwd, $this->procEnv, $this->procOptions);

            if (is_resource($process)) {
                $this->_stdOut = trim(stream_get_contents($pipes[1]));
                $this->_stdErr = trim(stream_get_contents($pipes[2]));
                fclose($pipes[1]);
                fclose($pipes[2]);

                $this->_exitCode = proc_close($process);

                if ($this->_exitCode!==0) {
                    $this->_error = $this->_stdErr ? $this->_stdErr : "Failed without error message: $command";
                    return false;
                }
            } else {
                $this->_error = "Could not run command $command";
                return false;
            }
        }

        $this->_executed = true;
        return true;
    }

    public function getOutput()
    {
        return $this->_stdOut;
    }

    public function getError()
    {
        return $this->_error;
    }

    public function getStdErr()
    {
        return $this->_stdErr;
    }

    public function getExitCode()
    {
        return $this->_exitCode;
    }
}

function getGitInformation(string $path)
{
    if (!\is_dir($path . DIRECTORY_SEPARATOR . '.git')) {
        return false;
    }

    $process = \proc_open(
        'git describe --tags',
        [
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w'],
        ],
        $pipes,
        $path
    );

    if (!\is_resource($process)) {
        return false;
    }

    $result = \trim(\stream_get_contents($pipes[1]));

    \fclose($pipes[1]);
    \fclose($pipes[2]);

    $returnCode = \proc_close($process);

    if ($returnCode !== 0) {
        return false;
    }

    return $result;
}

