        $response = $this->curlPost($url, $params);

        return json_decode($response, true);
    }

    public function curlPost($url = '', $postData = '', $options = []) {
        if (empty($url) || empty($postData)) {
            return false;
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($postData));
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);

        if (!empty($options)) {
            foreach ($options as $option => $value) {
                curl_setopt($ch, $option, $value);
            }
        }

        $response = curl_exec($ch);
        if ($response === false) {
            throw new Exception('Curl error: ' . curl_error($ch));
        }

        curl_close($ch);
        return $response;
    }

    public function generateSign($params, $signType = 'RSA') {
        $stringToSign = $this->getSignContent($params);
        return $this->sign($stringToSign, $signType);
    }

    public function sign($data, $signType = 'RSA') {
        $privateKey = $this->rsaPrivateKey;
        $res = openssl_pkey_get_private($privateKey);

        if (!$res) {
            throw new Exception('Private key invalid');
        }

        $digest = $signType === 'RSA2' ? OPENSSL_ALGO_SHA256 : OPENSSL_ALGO_SHA1;
        $signature = '';
        $success = openssl_sign($data, $signature, $res, $digest);
        openssl_free_key($res);

        if (!$success) {
            throw new Exception('Sign failed');
        }

        return base64_encode($signature);
    }

    public function checkEmpty($value) {
        return empty($value);
    }

    public function getSignContent($params) {
        unset($params['sign'], $params['file_content']);
        ksort($params);
        $stringToSign = '';

        foreach ($params as $k => $v) {
            if ($this->checkEmpty($v)) {
                continue;
            }
            $stringToSign .= $k . '=' . $v . '&';
        }

        return trim($stringToSign, '&');
    }

    public function characet($data, $targetCharset) {
        if (mb_detect_encoding($data, 'UTF-8, ASCII, ISO-8859-1') != $targetCharset) {
            return mb_convert_encoding($data, $targetCharset, 'UTF-8, ASCII, ISO-8859-1');
        }
        return $data;
    }
