class CurlDownloader {
    private $tempDir;
    private $outputFile;

    public function __construct() {
        $this->tempDir = sys_get_temp_dir();
    }

    public function setTempDir(string $tempDir): void {
        $this->tempDir = $tempDir;
    }

    public function download(string $url): bool {
        if (!file_exists($this->tempDir) || !is_dir($this->tempDir) || !is_writable($this->tempDir)) {
            throw new ErrorException("Invalid temporary directory");
        }

        $tempFile = tempnam($this->tempDir, 'curl_download_');
        if (!$tempFile) {
            throw new ErrorException("Failed to create temporary file");
        }

        $fp = fopen($tempFile, 'w');
        if (!$fp) {
            unlink($tempFile);
            throw new ErrorException("Failed to open temporary file");
        }

        $ch = curl_init($url);
        curl_setopt_array($ch, [
            CURLOPT_HEADER => false,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_TIMEOUT => 30,
            CURLOPT_FILE => $fp,
        ]);

        curl_exec($ch);

        $errno = curl_errno($ch);
        $error = curl_error($ch);

        curl_close($ch);
        fclose($fp);

        if ($errno) {
            unlink($tempFile);
            throw new ErrorException("cURL error ({$errno}): {$error}");
        }

        $this->outputFile = $tempFile;
        return true;
    }

    public function getFilename(): string {
        return $this->outputFile;
    }

    public function cleanup(): void {
        if ($this->outputFile && file_exists($this->outputFile)) {
            unlink($this->outputFile);
        }
    }
}

