        if (strpos($path, '/rsf') === 0) {
            $host = self::QINIU_RSF_HOST;
        } elseif (strpos($path, '/up') === 0) {
            $host = self::QINIU_UP_HOST;
        }
        $url = 'https://' . $host . '/' . ltrim($path, '/');

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);

        switch ($method) {
            case 'GET':
                curl_setopt($ch, CURLOPT_HTTPGET, true);
                break;
            case 'POST':
                curl_setopt($ch, CURLOPT_POST, true);
                if ($body !== null) {
                    curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
                }
                break;
            case 'PUT':
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'PUT');
                if ($body !== null) {
                    if (is_resource($body)) {
                        curl_setopt($ch, CURLOPT_UPLOAD, true);
                        curl_setopt($ch, CURLOPT_INFILE, $body);
                        $stat = fstat($body);
                        if ($stat && isset($stat['size'])) {
                            curl_setopt($ch, CURLOPT_INFILESIZE, $stat['size']);
                        }
                    } else {
                        curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
                    }
                }
                break;
            case 'HEAD':
                curl_setopt($ch, CURLOPT_NOBODY, true);
                break;
            default:
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
                if ($body !== null) {
                    curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
                }
        }

        if (is_array($headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }

        $response = curl_exec($ch);
        if ($response === false) {
            $this->error = 'cURL Error';
            $this->errorStr = curl_error($ch);
            curl_close($ch);
            return false;
        }

        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $header = substr($response, 0, $headerSize);
        $body = substr($response, $headerSize);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode >= 400) {
            $this->error($header, $body);
            return false;
        }

        if ($method === 'GET') {
            return $body;
        } else {
            return $this->response($response);
        }
    }

    public function response($text) {
        $parts = explode("\r\n\r\n", $text, 2);
        $body = isset($parts[1]) ? $parts[1] : $parts[0];
        $json = json_decode($body, true);
        return json_last_error() === JSON_ERROR_NONE ? $json : [];
    }

    public function error($header, $body) {
        preg_match('{HTTP/\S+ (\d+)}', $header, $match);
        $code = isset($match[1]) ? $match[1] : 'Unknown';
        $this->error = "HTTP $code";
        $data = $this->response($body);
        if (!empty($data) && isset($data['error'])) {
            $this->errorStr = $data['error'];
        } else {
            $this->errorStr = $body;
        }
    }
