class TimeoutException extends \Exception {}
class URLChecker {
    private $pollIntervalMs;
    private $connectTimeoutMs;
    public function __construct(int $pollIntervalMs = 1000, int $connectTimeoutMs = 5000) {
        $this->pollIntervalMs = $pollIntervalMs;
        $this->connectTimeoutMs = $connectTimeoutMs;
    }
    public function waitUntilAvailable(int $timeout_in_ms, string $url): self {
        $startTime = microtime(true);
        while (true) {
            $code = $this->getHTTPResponseCode($url);
            if ($code === 200) {
                return $this;
            }
            $elapsed = microtime(true) - $startTime;
            if ($elapsed >= $timeout_in_ms / 1000.0) {
                throw new TimeoutException("Timeout waiting for URL to become available: $url");
            }
            usleep($this->pollIntervalMs * 1000);
        }
    }
    public function waitUntilUnavailable(int $timeout_in_ms, string $url): self {
        $startTime = microtime(true);
        while (true) {
            $code = $this->getHTTPResponseCode($url);
            if ($code !== 200) {
                return $this;
            }
            $elapsed = microtime(true) - $startTime;
            if ($elapsed >= $timeout_in_ms / 1000.0) {
                throw new TimeoutException("Timeout waiting for URL to become unavailable: $url");
            }
            usleep($this->pollIntervalMs * 1000);
        }
    }
    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init();
        if ($ch === false) {
            return null;
        }
        curl_setopt_array($ch, [
            CURLOPT_URL => $url,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_HEADER => true,
            CURLOPT_NOBODY => true,
            CURLOPT_CONNECTTIMEOUT_MS => $this->connectTimeoutMs,
            CURLOPT_FOLLOWLOCATION => false,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_SSL_VERIFYHOST => false,
        ]);
        $response = curl_exec($ch);
        if ($response === false) {
            curl_close($ch);
            return null;
        }
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        return $httpCode;
    }
}

