    public function imageUrl(int $width, int $height, string|null $category, bool $randomize, string|null $word, bool $gray, string $format): string {
        $format = strtolower($format);
        $allowedFormats = [self::FORMAT_JPG, self::FORMAT_JPEG, self::FORMAT_PNG];
        if (!in_array($format, $allowedFormats)) {
            $format = self::FORMAT_PNG;
        }

        $url = self::BASE_URL . "/{$width}x{$height}.{$format}";
        $params = [];

        if ($category !== null) {
            $params['category'] = $category;
        }
        if ($word !== null) {
            $params['text'] = $word;
        }
        if ($gray) {
            $params['gray'] = 'true';
        }
        if ($randomize) {
            $params['_'] = microtime(true);
        }

        if (!empty($params)) {
            $url .= '?' . http_build_query($params);
        }

        return $url;
    }

    public function image(?string $dir = null, int $width = 640, int $height = 480, ?string $category = null, bool $fullPath = true, bool $randomize = true, ?string $word = null, bool $gray = false, string $format = self::FORMAT_PNG) {
        $format = strtolower($format);
        $allowedFormats = [self::FORMAT_JPG, self::FORMAT_JPEG, self::FORMAT_PNG];
        if (!in_array($format, $allowedFormats)) {
            $format = self::FORMAT_PNG;
        }

        $url = $this->imageUrl($width, $height, $category, $randomize, $word, $gray, $format);

        if ($dir === null) {
            $dir = sys_get_temp_dir();
        } else {
            if (!is_dir($dir) || !is_writable($dir)) {
                return false;
            }
        }

        $filename = uniqid('placeholder_', true) . '.' . $format;
        $filePath = $dir . DIRECTORY_SEPARATOR . $filename;

        $imageData = null;
        if (extension_loaded('curl')) {
            $ch = curl_init($url);
            curl_setopt_array($ch, [
                CURLOPT_RETURNTRANSFER => true,
                CURLOPT_FOLLOWLOCATION => true,
                CURLOPT_TIMEOUT => 10,
            ]);
            $imageData = curl_exec($ch);
            $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            curl_close($ch);

            if ($httpCode !== 200 || $imageData === false) {
                return false;
            }
        } else {
            if (!ini_get('allow_url_fopen')) {
                return false;
            }
            $imageData = @file_get_contents($url);
            if ($imageData === false) {
                return false;
            }
        }

        if (file_put_contents($filePath, $imageData) === false) {
            return false;
        }

        return $fullPath ? $filePath : $filename;
    }
}

